import React, { useState } from 'react';

import './App.css';

import Logo from "./Logo.js"
import InputBox from './TextInput';
import Map from './MapComponent.js';
import RadiusBox from './radiusBox.js';
import {DB_Actions } from './db_actions.js';
import DB_Modal from './db_modal.js';

URL = "http://localhost:7878"

const InputSection = ({URL, handleChange, handleClick, setIsPlane, setExpandedIndex,  hostileInfo, setPlaneData, setHostileBackup, setPlaneBackup }) => {
  return(
  <div className='InputSection'>
    <InputBox InputName={"Latitude"} DisplayName={"קו רוחב"} Data={handleChange} />,
    <InputBox InputName={"Longitude"} DisplayName={"קו אורך"} Data={handleChange} />,
    <InputBox InputName={"Radius"} DisplayName={"רדיוס טיסה"} Data={handleChange} />,
    <InputBox InputName={"Speed"} DisplayName={"מהירות"} Data={handleChange} />,
    <button onClick={() => {
      setIsPlane(false);
      setExpandedIndex(-1);
      handleClick();
      setIsPlane(true);
    }}>שלח מידע</button>
  </div>);
}

const App = () => {

  const [planeData, setPlaneData] = useState([]);
  const [isPlane, setIsPlane] = useState(false);
  const [expandedIndex, setExpandedIndex] = useState(-1);
  const [scroll, setScroll] = useState(false);
  const [hostileInfo, setHostileInfo] = useState({
    Latitude: '',
    Longitude: '',
    Radius: '',
    Speed: ''
  });
  const [showInput, setShowInput] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [saved, setSaved] = useState(false);

  const [hostileBackup, setHostileBackup] = useState({});
  const [planeBackup, setPlaneBackup] = useState([])
  const [savedBackup, setSavedBackup] = useState(false);

  const openModal = () => {
    setShowModal(true);
  }
  const closeModal = () => {
    setShowModal(false);
  }

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHostileInfo(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  return (
    <div className='App'>

      <Logo />

      {showInput && (<InputSection handleChange={handleChange} handleClick={getPlanesFromAPI}/>)}
      
      <div className='mapContainer'>
        {isPlane && (
          <RadiusBox planes={planeData} expandedIndex = {expandedIndex} setExpandedIndex={setExpandedIndex}
          scroll={scroll} setScroll={setScroll}/>
        )}
        
        {isPlane && (
          <Map hostileInfo={hostileInfo} friendlyInfo={planeData} expanded_index={expandedIndex}
          setExpandedIndex={setExpandedIndex} setScroll={setScroll}/>)}
          
      </div>
      
      <div className='DB_Section'>
        {isPlane && (<DB_Actions URL={URL} hostileInfo={hostileInfo} friendlyInfo={planeData} 
        openModal={openModal} saved = {saved} setSaved={setSaved} setSavedBackup={setSavedBackup}/>)}
        
        <DB_Modal showModal={showModal} onCloseModal={closeModal} URL={URL} setShowInput={setShowInput} 
        setHostileInfo={setHostileInfo} setPlaneData={setPlaneData} setIsPlane={setIsPlane}
        setSaved = {setSaved}/>
        {/* Couldnt fix the awful margin sorry :( */}
        {!showInput && (<button onClick={() => {
          setIsPlane(false);
          setExpandedIndex(-1);
          setPlaneData(planeBackup);
          setHostileInfo(hostileBackup);
          setIsPlane(true);
          setSaved(savedBackup);
          setShowInput(true);
        }}>חזור</button>)}
      </div>
      
    </div>
  );

}
export default App;
