import React, { useState, useEffect } from 'react';

const Modal = ({ isOpen, onClose, data, URL }) => {
    const [amount, setAmount] = useState(10);
    const [operations, setOperations] = useState([]);
    if (!isOpen) return null;
    
    const getOperations = async (amount) => {
        const operations = await fetch(`${URL}/operation?amount=${amount}`);
          // Handle response
          if (operations.ok) {
            const response = await operations.json();
            setOperations(response.operations)
          } else {
            // Error saving operation
            console.error('Failed to import operations');
          }
    }

    const changeAmount = (e) => {
        const amount = e.target.value;
        setAmount(amount);
    }
    
    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <div className='modalInputHolder'>
                    <div className='ModalInput'>
                        <h1 className='InputTitle'>כמות מבצעים</h1>
                        <input className='InputBox' onChange={changeAmount}/>
                    </div>
                    <button onClick={ async() => {
                            await getOperations(amount);
                        }}>Load</button>
                </div>
                <div className="operations-container">
                    {operations.map((operation) => (
                    <div key={operation.id} className="operation-box">
                        <div style={{display: 'flex', flexDirection: 'column', width: 200}}>
                            <h3>Operation ID: {operation.id} <br/> Plane Count: {operation.planes.friendlyInfo.length} </h3>
                            <p> 
                                Coordinates: {operation.hostile_plane.hostileInfo.Latitude}, {operation.hostile_plane.hostileInfo.Longitude}
                            <br/> Speed: {operation.hostile_plane.hostileInfo.Speed} 
                            <br/> Radius: {operation.hostile_plane.hostileInfo.Radius} <br/> 
                            </p>
                        </div>
                        <button style={{marginLeft: 1}}>Import Operation</button>
                    </div>
                ))}
                </div>
            </div>
        </div>
    );
};
 
export default Modal;
