//               <Popup>{'Katbam'}</Popup>
//             </Marker>
//             <Circle center={[hostileinfo['Latitude'], hostileinfo['Longitude']]} radius={hostileinfo['Radius']} />
//           </>
//         )}

//         {friendlyinfo.map((plane, index) => (
//           <Marker key={index} position={[plane.latitude, plane.longitude]}>
//             <Popup>{plane.description}</Popup>
//           </Marker>
//         ))}
//       </MapContainer>
//     </div>
//   );
// }

const App = () => {

  const [planeData, setPlaneData] = useState([])
  const [isPlane, setIsPlane] = useState(false)

  const [hostlieInfo, setHostileInfo] = useState({
    Latitude: '',
    Longitude: '',
    Radius: '',
    Speed: ''
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHostileInfo(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  const handleClick = async () => {
    setIsPlane(false);
    const requestURL = `${URL}plane/getPlanes/${hostlieInfo['Latitude']}&${hostlieInfo['Longitude']}&${parseFloat(hostlieInfo['Radius'])* 0.539956803}`;
    const request = await fetch(requestURL);
    const response = await request.json();
    setPlaneData(response);
    setIsPlane(true);
    console.log(response);
  };

  return (
    <div className='App'>

      <Logo />

      <div className='InputSection'>
        <InputBox InputName={"Latitude"} DisplayName={"קו רוחב"} Data={handleChange} />
        <InputBox InputName={"Longitude"} DisplayName={"קו אורך"} Data={handleChange} />
        <InputBox InputName={"Radius"} DisplayName={"רדיוס טיסה"} Data={handleChange} />
        <InputBox InputName={"Speed"} DisplayName={"מהירות"} Data={handleChange} /> 
        <button onClick={handleClick}>שלח מידע</button>
      </div>

      <div>
        {/* {planeData.length>0 && (
          <DisplayMap hostileinfo={hostlieInfo} friendlyinfo={planeData}/>
        )} */}
        {isPlane && (
        <Map hostileInfo={hostlieInfo} friendlyInfo={planeData}/>)}
      </div>

    </div>
  );

}
export default App;
