import React, { useEffect, useState } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = ({ friendlyPlanes, hostilePlane }) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (!map) {
      const newMap = L.map('map').setView([hostilePlane.latitude, hostilePlane.longitude], 10);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; OpenStreetMap contributors'
      }).addTo(newMap);

      setMap(newMap);
    } else {
      // If map already exists, just update the view
      map.setView([hostilePlane.latitude, hostilePlane.longitude]);
    }
  }, [map, hostilePlane]);

  useEffect(() => {
    if (map) {
      // Clear previous markers and circles
      map.eachLayer(layer => {
        if (layer instanceof L.Marker || layer instanceof L.Circle) {
          map.removeLayer(layer);
        }
      });

      // Add hostile plane marker
      L.marker([hostilePlane.latitude, hostilePlane.longitude])
        .bindPopup('Hostile Plane')
        .addTo(map);

      // Add circle representing hostile plane's radius
      L.circle([hostilePlane.latitude, hostilePlane.longitude], {
        radius: hostilePlane.radius * 1000, // Convert radius from km to meters
        color: 'red',
        opacity: 0.5,
        fillOpacity: 0.2
      }).addTo(map);

      // Add friendly planes markers
      friendlyPlanes.forEach(plane => {
        L.marker([plane.latitude, plane.longitude])
          .bindPopup(plane.description)
          .addTo(map);
      });
    }
  }, [map, friendlyPlanes, hostilePlane]);

  return <div id="map" style={{ height: '500px' }} />;
};

export default MapComponent;
