import React from 'react';
import { useEffect, useState } from 'react';
import './App.css';
import Logo from "./Logo.js"
import InputBox from './TextInput';

URL = "http://localhost:7878/drone/coords/1&2"

const App = () => {

  // const [data, setData] = useState(null)

  // useEffect( () => {
