const DB_Modal = ({ showModal, onCloseModal }) => {
    const [data, setData] = useState(null);

    useEffect(() => {
        if (showModal) {
            setData('Hello Modal!');
        }
    }, [showModal]);

    if (!showModal) {
        return null;
    }

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onCloseModal}>&times;</span>
                {data ? (
                    <div>
                        {/* Render your data here */}
                        <h1>{data.title}</h1>
                        <p>{data.description}</p>
                    </div>
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </div>
    );
};

export default DB_Modal;
