import React from 'react';
import { useEffect, useState } from 'react';
import './App.css';
import Logo from "./Logo.js"
import InputBox from './TextInput';
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

URL = "http://localhost:7878/"

const DisplayMap = ({hostlieInfo, friendlyInfo}) => {

  console.log(hostlieInfo);

  console.log(friendlyInfo);
  return (
    <div className="map-container">
      <MapContainer center={[hostlieInfo['latitude'], hostlieInfo['longitude']]} zoom={10} style={{ height: '600px', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {/* Display hostile plane */}
        {hostlieInfo && (
          <>
            <Marker position={[hostlieInfo['latitude'], hostlieInfo['longitude']]}>
              <Popup>{hostlieInfo.description}</Popup>
            </Marker>
            <Circle center={[hostlieInfo['latitude'], hostlieInfo['longitude']]} radius={hostlieInfo.radius} />
          </>
        )}

        {friendlyInfo.map((plane, index) => (
          <Marker key={index} position={[plane.latitude, plane.longitude]}>
            <Popup>{plane.description}</Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

const App = () => {

  const [planeData, setPlaneData] = useState(null)
  const [isPlane, setIsPlane] = useState(false)

  const [hostlieInfo, setHostileInfo] = useState({
    Latitude: '',
    Longitude: '',
    Radius: '',
    Speed: ''
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHostileInfo(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  const handleClick = async () => {
    const requestURL = `${URL}plane/getPlanes/${hostlieInfo['Latitude']}&${hostlieInfo['Longitude']}&${hostlieInfo['Radius']}`;
    const request = await fetch(requestURL);
    const response = await request.json();
    setPlaneData(response);
    setIsPlane(true);
    console.log(response);
  };

  return (
    <div className='App'>

      <Logo />

      <div className='InputSection'>
        <InputBox InputName={"Latitude"} DisplayName={"קו רוחב"} Data={handleChange} />
        <InputBox InputName={"Longitude"} DisplayName={"קו אורך"} Data={handleChange} />
        <InputBox InputName={"Radius"} DisplayName={"רדיוס טיסה"} Data={handleChange} />
        <InputBox InputName={"Speed"} DisplayName={"מהירות"} Data={handleChange} /> 
        <button onClick={handleClick}>שלח מידע</button>
      </div>

      <div>
      {isPlane ? <DisplayMap hostlieInfo={hostlieInfo} friendlyInfo={planeData}/> : <div>No data available</div>}
      </div>

    </div>
  );

}
export default App;
