import React, { useState } from 'react';
import getOperations from './getOperations';

const ModalInputHolder = ({URL, changeAmount, setOperations, amount}) => {
    return (
        <div className='modalInputHolder'>
            <div className='ModalInput' style={{marginLeft: 25}}>
                <h1 className='InputTitle'>כמות מבצעים</h1>
                <input className='InputBox' onChange={changeAmount}/>
            </div>
            <button onClick={ async() => {
                await getOperations(URL, setOperations, amount);
            }}>Load</button>
        </div>
    );
}

const Modal = ({ isOpen, onClose, URL, setShowInput, setHostileInfo, setPlaneData, setIsPlane, setSaved}) => {
    const [amount, setAmount] = useState(10);
    const [operations, setOperations] = useState([]);
    if (!isOpen) return null;
    
    const changeAmount = (e) => {
        const amount = e.target.value;
        setAmount(amount);
    }
    
    const importOperation = (id) => {
        console.log(id);
        setIsPlane(false);

        setHostileInfo(operations[id].hostile_plane.hostileInfo);
        setPlaneData(operations[id].planes.friendlyInfo);
        setSaved(true);
        setShowInput(false);
        
        setIsPlane(true);
    }

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <ModalInputHolder URL={URL} changeAmount={changeAmount} setOperations={setOperations} amount={amount}/>
                
            </div>
        </div>
    );
};
 
export default Modal;
