import React, { useState, useEffect } from 'react';


const Modal = ({ isOpen, onClose, URL, setShowInput, setHostileInfo, setPlaneData, setIsPlane, setSaved}) => {
    const [amount, setAmount] = useState(10);
    const [operations, setOperations] = useState([]);
    if (!isOpen) return null;
    
    const getOperations = async (amount) => {
        try{    
            const operations = await fetch(`${URL}/operation?amount=${amount}`);
            if (operations.ok) {
                const response = await operations.json();
                setOperations(response.operations);
                console.log(response.operations);
            } else {
                // Error recieving the operations from the DB
                alert('Failed to recive the operations');
            }}
            catch(error) {
                console.error('Error:', error);
            alert('Error in saving the operations, check the console for more info.')
            }
    }

    const changeAmount = (e) => {
        const amount = e.target.value;
        setAmount(amount);
    }
    
    const importOperation = (id) => {
        console.log(id);
        setIsPlane(false);

        setHostileInfo(operations[id].hostile_plane.hostileInfo);
        setPlaneData(operations[id].planes.friendlyInfo);
        setSaved(true);
        setShowInput(false);
        
        setIsPlane(true);
    }

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <div className='modalInputHolder'>
                    <div className='ModalInput' style={{marginLeft: 25}}>
                        <h1 className='InputTitle'>כמות מבצעים</h1>
                        <input className='InputBox' onChange={changeAmount}/>
                    </div>
                    <button onClick={ async() => {
                            await getOperations(amount);
                        }}>Load</button>
                </div>
                <div className="operations-container">
                    {operations.map((operation) => (
                    <div key={operation.id} className="operation-box">
                        <div style={{display: 'flex', flexDirection: 'column'}}>
                            <h3>ID: {operation.id} <br/> Planes: {operation.planes.friendlyInfo.length}</h3>
                            <p> 
                                Coordinates: {operation.hostile_plane.hostileInfo.Latitude}, {operation.hostile_plane.hostileInfo.Longitude}
                            <br/> Speed: {operation.hostile_plane.hostileInfo.Speed} 
                            <br/> Radius: {operation.hostile_plane.hostileInfo.Radius} <br/> 
                            <br/> Timestamp: {operation.created_at}
                            </p>
                        </div>
                        <button onClick={() => {
                            importOperation(operation.id-1);
                        }}>Import Operation</button>
                    </div>
                ))}
                </div>
            </div>
        </div>
    );
};
 
export default Modal;
