import React, { useEffect, useState, forwardRef } from 'react';
import './App.css';

const PlaneCard = forwardRef(({index, plane, expandedIndex, setExpandedIndex}) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpand = () => {
    setExpanded(!expanded);
    (expanded) ? setExpandedIndex(-1):setExpandedIndex(index);
    console.log(index);    
  };

  useEffect( ()=>{
    if (expandedIndex != index){
        setExpanded(false);
    }
    else {
        setExpanded(true);
    }
  }, [expandedIndex] );

  return (
    <div className="planeCard" onClick={toggleExpand}>
      <h1>{plane.description}</h1>
      {expanded && (
        <div>
          <p>Latitude: {plane.latitude}</p>
          <p>Longitude: {plane.longitude}</p>
          <p>Speed: {plane.air_speed_in_knots} knots</p>
          <p>Track: {plane.true_track}</p>
          <p>Squawk: {plane.squawk}</p>
          <p>Category: {plane.category}</p>
        </div>
      )}
    </div>
  );
});

export default PlaneCard;
