import React from 'react';
import { useEffect, useState } from 'react';
import './App.css';
import Logo from "./Logo.js"
import InputBox from './TextInput';

import Map from './MapComponent.js';

import "leaflet/dist/leaflet.css";

import "leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css";

import "leaflet-defaulticon-compatibility";
// END: Preserve spaces to avoid auto-sorting
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";

// import "leaflet/dist/leaflet.css";

// import "leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css";

// import "leaflet-defaulticon-compatibility";

// import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';

URL = "http://localhost:7878/"

const DisplayMap = ({hostileinfo, friendlyinfo}) => {
  console.log('here');
  console.log(hostileinfo);
  console.log(friendlyinfo);
  return (hostileinfo != null && friendlyinfo != null) ? (
    <div className="map-container">
      <MapContainer center={[46, 17]} zoom={10} style={{ height: '600px', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {hostileinfo && (
          <>
            <Marker position={[hostileinfo['Latitude'], hostileinfo['Longitude']]}>
              <Popup>{'Katbam'}</Popup>
            </Marker>
            <Circle center={[hostileinfo['Latitude'], hostileinfo['Longitude']]} radius={hostileinfo['Radius']} />
          </>
        )}

        {friendlyinfo.map((plane, index) => (
          <Marker key={index} position={[plane.latitude, plane.longitude]}>
            <Popup>{plane.description}</Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  ):(<div></div>);
}

const App = () => {

  const [planeData, setPlaneData] = useState([])
  const [isPlane, setIsPlane] = useState(false)

  const [hostlieInfo, setHostileInfo] = useState({
    Latitude: '',
    Longitude: '',
    Radius: '',
    Speed: ''
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setHostileInfo(prevState => ({
        ...prevState,
        [name]: value
    }));
  };

  const handleClick = async () => {
    const requestURL = `${URL}plane/getPlanes/${hostlieInfo['Latitude']}&${hostlieInfo['Longitude']}&${hostlieInfo['Radius']}`;
    const request = await fetch(requestURL);
    const response = await request.json();
    setPlaneData(response);
    setIsPlane(true);
    console.log(response);
  };

  return (
    <div className='App'>

      <Logo />

      <div className='InputSection'>
        <InputBox InputName={"Latitude"} DisplayName={"קו רוחב"} Data={handleChange} />
        <InputBox InputName={"Longitude"} DisplayName={"קו אורך"} Data={handleChange} />
        <InputBox InputName={"Radius"} DisplayName={"רדיוס טיסה"} Data={handleChange} />
        <InputBox InputName={"Speed"} DisplayName={"מהירות"} Data={handleChange} /> 
        <button onClick={handleClick}>שלח מידע</button>
      </div>

      <div>
        {/* {planeData.length>0 && (
          <DisplayMap hostileinfo={hostlieInfo} friendlyinfo={planeData}/>
        )} */}
        <Map />
      </div>

    </div>
  );

}
export default App;
